import React, { useState } from 'react';
import './Counter.css';

function Counter() {
  const [count, setCount] = useState(0);
  const [background, setBackground] = useState('./assets/plus.jpg'); // Изначальное изображение

  const increment = () => {
    setCount(count + 1);
    setBackground('./assets/sun.jpg'); // Меняем фон на изображение для "+";
  };

  const decrement = () => {
    setCount(count - 1);
    setBackground('./assets/minus.jpg'); // Меняем фон на изображение для "-";
  };

  const reset = () => {
    setCount(0);
    setBackground('./assets/plus.jpg'); // Можно установить любое изображение по умолчанию
  };

  return (
    <div className="counter-container" style={{ backgroundImage: `url(${background})` }}>
      <h1 className="counter-display">Counter: {count}</h1>
      <div className="counter-buttons">
        <button onClick={increment} className="counter-button increment">+</button>
        <button onClick={decrement} className="counter-button decrement">-</button>
        <button onClick={reset} className="counter-button reset">Reset</button>
      </div>
    </div>
  );
}

export default Counter;
