import React, { useState, useCallback } from 'react';
import './Counter.css';


function Counter() {
  const initialCount = 0; 
  const [count, setCount] = useState(initialCount);

  // Функция для увеличения счетчика
  const handleIncrement = useCallback(() => {
    setCount(prevCount => prevCount + 1);
  }, []);

  // Функция для уменьшения счетчика
  const handleDecrement = useCallback(() => {
    setCount(prevCount => prevCount - 1);
  }, []);

  // Функция для сброса счетчика
  const handleReset = useCallback(() => {
    setCount(initialCount);
  }, []);

  return (
    <div className="counter-container">
      <h1 className="counter-display">Counter: {count}</h1>

        <button onClick={onDecrement} className="counter-button decrement">-</button>
        <button onClick={onIncrement} className="counter-button increment">+</button>
        <button onClick={onReset} className="counter-button reset">Reset</button>
      </div>
  );
}

export default Counter;
